import React, { useState } from 'react';
import data from './verbs.json';
import Button from 'react-bootstrap/Button'
import { Container } from 'react-bootstrap';
import Table from 'react-bootstrap/Table'

const Grammar = () => {
    const [selectedVerb, setSelectedVerb] = useState(data.verbs[0].infinitive); 

    const handleVerbClick = (infinitive) => {
      setSelectedVerb(infinitive);
    };

    return (
      <Container className='mt-5'>
        <div className='mt-5 text-center d-flex justify-content-center'>
          {data.verbs.map((verb) => (
            <Button 
                className="m-2 mt-5"
                variant={verb.infinitive === selectedVerb ? "success" : "outline-success"} 
                key={verb.infinitive}
                onClick={() => handleVerbClick(verb.infinitive)}>
                {verb.infinitive}
            </Button>
          ))}
        </div>
        {selectedVerb && (
          <div className='text-center'>
            <h2>{selectedVerb}</h2>
            <Table bordered hover className='w-50 mx-auto'>
                <thead>
                    <tr>
                        <th>Subject</th>
                        <th>Conjugation</th>
                    </tr>
                </thead>
                <tbody>
                    {Object.entries(
                    data.verbs.find((verb) => verb.infinitive === selectedVerb).conjugations
                    ).map(([subject, conjugation]) => (
                        <tr key={subject}>
                            <td>{subject}</td>
                            <td>{conjugation}</td>
                        </tr>
                    ))}
                </tbody>
            </Table>
          </div>
        )}
      </Container>
    );
  };

  export default Grammar;
