import React, { useState } from 'react';
import data from './verbs.json';


const Grammar = () => {
    const [selectedVerb, setSelectedVerb] = useState(data.verbs[0].infinitive); // Set the first verb as active by default

    const handleVerbClick = (infinitive) => {
      setSelectedVerb(infinitive);
    };

    return (
      <div>
        <h1>Verbs</h1>
        <div>
          {data.verbs.map((verb) => (
            <button
              key={verb.infinitive}
              onClick={() => handleVerbClick(verb.infinitive)}
            >
              {verb.infinitive}
            </button>
          ))}
        </div>
        {selectedVerb && (
          <div>
            <h2>{selectedVerb}</h2>
            <table>
              <thead>
                <tr>
                  <th>Subject</th>
                  <th>Conjugation</th>
                </tr>
              </thead>
              <tbody>
                {Object.entries(
                  data.verbs.find((verb) => verb.infinitive === selectedVerb).conjugations
                ).map(([subject, conjugation]) => (
                  <tr key={subject}>
                    <td>{subject}</td>
                    <td>{conjugation}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    );
  };

  export default Grammar;
