import React, { useState } from 'react';
import verbs from './verbs.json';

const Grammar = () => {
  // Assuming you have imported your JSON with verb conjugations here

  const [selectedVerb, setSelectedVerb] = useState(null);

  const handleVerbClick = (infinitive) => {
    setSelectedVerb(infinitive);
  };

  return (
    <div>
      <h1>Verbs</h1>
      <div>
        {verbs.map((verb) => (
          <button
            key={verb.infinitive}
            onClick={() => handleVerbClick(verb.infinitive)}
          >
            {verb.infinitive}
          </button>
        ))}
      </div>
      {selectedVerb && (
        <div>
          <h2>{selectedVerb}</h2>
          <table>
            <thead>
              <tr>
                <th>Subject</th>
                <th>Conjugation</th>
              </tr>
            </thead>
            <tbody>
              {verbs.find((verb) => verb.infinitive === selectedVerb).conjugations.map((conjugation) => (
                <tr key={conjugation[0]}>
                  <td>{conjugation[0]}</td>
                  <td>{conjugation[1]}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Grammar;
